    private void ButtonBinerActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_ButtonBinerActionPerformed
        // TODO add your handling code here:
         int desi = Integer.valueOf(Desimal.getText());
         String bin = Integer.toBinaryString(desi);
         Biner.setText(bin);
    }//GEN-LAST:event_ButtonBinerActionPerformed

