    private void ButtonDesimalActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_ButtonDesimalActionPerformed
        // TODO add your handling code here:
         String biner = Biner.getText();
         int des = Integer.parseInt(biner, 2);
         Desimal.setText(String.valueOf(des));
    }//GEN-LAST:event_ButtonDesimalActionPerformed

